AJS.$(document).ready(function(){AJS.$("#ai-transform-button").click(function(){var b=AJS.$("#description").val();console.log("Button clicked. Description:",b);AJS.$.ajax({url:"http://dcalda2715:11434/api/generate",type:"POST",data:JSON.stringify({description:b}),contentType:"application/json",success:function(a){console.log("API Response:",a);AJS.$("#description").val(a.transformedDescription)},error:function(a){console.error("Error:",a)}})})});